mapper.select:
select s.id pointId, s.point, s.project_code, s.card_no, s.gateway_serial, s.total_flow, c.company_name, wdr.p26 battery, wdr.p27 network, b.full_battery fullBattery, wdr.* from wt_station s left join wt_data d on s.gateway_serial = d.mcu left join wt_company c on s.company_id = c.id left join wt_station_base b on s.id = b.station_id LEFT JOIN wt_data_raw wdr ON wdr.receive_time = d.receive_time AND wdr.mcu = d.mcu
and s.id in
${pointId}

select s.id pointId, s.point, s.project_code, s.card_no, s.gateway_serial, s.total_flow, c.company_name, d.p26 battery, d.p27 network, b.full_battery fullBattery, d.* from wt_data_raw d left join wt_station s on s.gateway_serial = d.mcu left join wt_company c on s.company_id = c.id left join wt_station_base b on s.id = b.station_id
s.point is not null
and d.time >= #{startTime}
and d.time <= #{endTime}
and s.id in
${pointId}
order by d.time desc

select
ifnull(${param},0) as ${param}
, time from wt_data_raw where mcu=#{mcu} and time >= #{startTime} and time <= #{endTime} order by time asc

    	select
    		mcu,

				${param} as ${param}

   		from
   			wt_data
 		where mcu = (
 			select gateway_serial from wt_station where id = #{stationId}
 		) and date(now()) = date(time)

    	select
    		mcu,

				avg(${param}) as ${param}

   		from
   			wt_data_raw
 		where mcu = (
 			select gateway_serial from wt_station where id = #{stationId}
 		) and time > adddate(now(),interval -1 month)

select
mcu,

	avg(${param}) as ${param}

from
wt_data_raw
where mcu = (
select gateway_serial from wt_station where id = #{stationId}
) and time > adddate(now(),interval -24 HOUR)

    	select * from (
    		select
    			mcu,
    			time,

					avg(${param}) as ${param}


    			, date_format(time,'%H') as dftime


    			, date_format(time,'%d') as dftime


    			, date_format(time,'%d') as dftime


    			, date_format(time,'%m') as dftime

    		from wt_data_raw
    		where mcu = #{mcu}

    				and date(time) = #{dateStr}


    				and date(time) <= #{dateStr}
					and date(time) >= date_add(#{dateStr}, INTERVAL -7 DAY)


    				and date_format(time,'%Y-%m') = #{dateStr}


    				and date_format(time,'%Y') = #{dateStr}

   			group by dftime
    	) a
    	order by a.time desc
