mapper.select:
select p.id, p.param, IF(m.alias_param_name is not null and m.alias_param_name!='',m.alias_param_name,p.param_name) param_name, IFNULL(m.alias_unit,p.unit) unit, p.round_type, m.display, p.round, p.quota_type, p.heichou, p.dibiao, p.involved FROM wt_param p RIGHT JOIN (SELECT param, display, alias_param_name, alias_unit FROM wt_station_monitor
and display = '1' and station_id=#{stationId}
) m on m.param = p.param ORDER BY id

select p.id, p.param, IF(m.alias_param_name is not null and m.alias_param_name!='',m.alias_param_name,p.param_name) param_name, IFNULL(m.alias_unit,p.unit) unit, p.round_type, m.display, p.round, p.quota_type, p.heichou, p.dibiao, p.involved FROM wt_param p RIGHT JOIN (SELECT param, display, alias_param_name, alias_unit FROM wt_station_monitor
and station_id=#{stationId}
) m on m.param = p.param ORDER BY id

    	select 
    		${column},time dateFormatStr,'0' dataType
    	from wt_data_raw where mcu=#{mcu}
		AND  time BETWEEN #{startTime} AND #{endTime}
		ORDER BY time DESC

select ${paramColumn},'0' dataType
from wt_data_raw where mcu=#{mcu}
AND time BETWEEN #{startTime} AND #{endTime}
GROUP BY mcu

mapper.update:
    	update wt_param set
    		id = #{id}

    			,param = #{param}


    			,param_name = #{paramName}


    			,unit = #{unit}


    			,display = #{display}


				,round_type = #{roundType}


				,heichou = #{heichou}


				,dibiao = #{dibiao}


				,involved = #{involved}

    	where id=#{id}

